"""
Java program to print prime numbers between 1 and 100
"""

public class Main {

    public static void main(String[] args) {
        int i, j, flag;
        for (i = 1; i <= 100; i++) {
            if (i == 1 || i == 0)
                continue;
            flag = 1;

            for (j = 2; j <= i / 2; ++j) {
                if (i % j == 0) {
                    flag = 0;
                    break;
                }
            }
            if (flag == 1)
                System.out.println(i);
        }
    }
}

def is_palindrome(input_word):
    input_word = input_word.lower()
    reverse_word = input_word[::-1]
    if input_word == reverse_word:
        return True
    else:
        return False
